const health = runtimeScene.getVariables().get("data_quality_current").getAsNumber()
const maxHealth = runtimeScene.getVariables().get("data_quality_max").getAsNumber()

let healthPercentage = health / maxHealth
healthPercentage = Math.min(Math.max(healthPercentage, 0), 1)

const healthBar = objects[0]
const originalHeight = healthBar.getVariables().get("original_height").getAsNumber()
const height = originalHeight * healthPercentage

const t = 0.93

const interoplatedValue = healthBar.getHeight() * t + height * (1-t)
healthBar.setHeight(interoplatedValue)

// find attached transactions over the target area
const transactions = runtimeScene.getObjects("transaction")
const targets = transactions.filter(transaction => {
    const rotation = transaction.getVariables().get("current_rotation").getAsNumber()
    const rotated_far_enough = rotation < -260

    const attached = transaction.getVariables().get("attached").getAsString() == "true"

    return rotated_far_enough && attached
})

if (targets.length === 0) {
    return
}

// get the shape
const target = targets[0]
const target_frame = target.getAnimationFrame()
const button_frame = objects[0].getVariables().get("shape_id").getAsNumber()

// enrich the transaction if the right button was pressed
if (target_frame === button_frame) {
    target.getBehavior("transaction")._behaviorData["enriched_correctly"] = true
}

const obj = objects[0]
let transaction = runtimeScene.linkedObjectsManager.getObjectsLinkedWith(obj)

if (transaction.length === 0) {
    return
}

transaction = transaction[0]

const xPos = transaction.getAABB().min[0]
const yPos = (transaction.getAABB().min[1] + transaction.getAABB().max[1]) / 2

obj.setX(xPos)
obj.setY(yPos)
